"use strict";

var createServer = require("./server"),
    Log = require("log"),
    log = new Log("info");

createServer().then(function (server) {
  var port = server.address().port;
  log.info("Commuter server listening on port " + port);
}).catch(function (e) {
  console.error(e);
  console.error(e.stack);
  process.exit(-10);
});